var path  = require( "path" );
var YELLO = '\033[33m';
var RED   = '\033[31m';
var GREEN = '\033[32m';
var RESET = '\033[0m';
var OK    = GREEN;
var NG    = RED;

exports.get = function(name){
  return {
    info    : console.log.bind( console, name ),
    error   : console.log.bind( console, name, NG + "(NG)" + RESET ),
    success : console.log.bind( console, name, OK + "(OK)" + RESET )
  };
};

exports.getByFliename = function(filename) {
  return exports.get( path.basename(filename, path.extname(filename) ) + ":" );
};

